import os.path
import configparser
import psycopg2

def connect(db):
    inifile = os.getenv('DATABASEINI', os.path.expanduser("~/.database.ini"))
    config = configparser.ConfigParser()
    config.read(inifile)
    p = config[db]
    return psycopg2.connect(database=db,
            host=p['host'], user=p['user'], password=p['password'])

